CREATE TABLE Evenement(
    id number(8) GENERATED BY DEFAULT ON NULL AS IDENTITY constraint pk_event_eventid primary key,
    titre varchar(80) NOT NULL,
    dateEvent Date NOT NULL,
    details clob,
    presence varchar(15) NOT NULL,
    constraint chk_event_presence check(presence in ('Obligatoire','Optionnel'))
);

ALTER TABLE PresenceEvenement MODIFY (
    EvenementID number(8),
    FOREIGN KEY(EvenementID) REFERENCES Evenement(id)
);

